import React,{useState, useEffect} from 'react';
import SearchIcon from '@material-ui/icons/Search';
import './Search.css';
import SpotifyWebApi from 'spotify-web-api-js';
import SongRow from './SongRow';
import Footer from './Footer';
import MenuIcon from '@material-ui/icons/Menu';

const Search = ({sideBar, setSideBar, smallScreen}) => {

    const spotify = new SpotifyWebApi();

    const [search, setSearch] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [playingTrack, setPlayingTrack] = useState();

    function chooseTrack(track){
        setPlayingTrack(track)
        setSearch('');
    }

    useEffect(()=>{
        if(!search) return
        let cancel = false;
        spotify.searchTracks(search).then(res => {
                if(cancel) return
            setSearchResults( res.tracks.items.map(track => track) )    
            })
      return () => cancel=true;
    },[search]);


    return (
        <div className={smallScreen?"SearchBack active":"SearchBack"}>
            <div className="top">
            {!sideBar?<MenuIcon onClick={()=>setSideBar(!sideBar)}/>:''}
            <div className="header_left">
                <SearchIcon />
                <input onChange={(e)=>setSearch(e.target.value)} placeholder="Search for Artists, Songs, Podcasts" type="text" />
            </div>
            </div>

            <div className="searchSongs">
                {
                    searchResults.map(track => (
                        <SongRow track={track} chooseTrack={chooseTrack}/>
                    ))
                }
            </div>

            <section class="section">
    <h2 class="title">Τα κορυφαία σου είδη</h2>
    <div class="section--body">
        <div class="section--body--item section--body--item--lg category--item">
            <a href="#">
                <h3>Hip Hop</h3>
                <img src="http://via.placeholder.com/150x150" alt=""/>
            </a>
        </div>
        <div class="section--body--item section--body--item--lg category--item">
            <a href="#">
                <h3>Pop</h3>
                <img src="http://via.placeholder.com/150x150" alt=""/>
            </a>
        </div>
    </div>

    <h2 class="title">Περιήγηση σε όλα</h2>
    <div class="section--body">
        <div class="section--body--item category--item">
            <a href="#">
                <h3>Podcast</h3>
                <img src="http://via.placeholder.com/150x150" alt=""/>
            </a>
        </div>

        <div class="section--body--item category--item" style="background-color: rgb(160, 195, 210);">
            <a href="#">
                <h3>Ειδικά για εσένα</h3>
                <img src="http://via.placeholder.com/150x150" alt=""/>
            </a>
        </div>

        <div class="section--body--item category--item" style="background-color: rgb(160, 195, 210);">
            <a href="#">
                <h3>Νέες Κυκλοφορίες</h3>
                <img src="http://via.placeholder.com/150x150" alt=""/>
            </a>
        </div>
    </div>
</section>
            <Footer trackUri={playingTrack?.uri}/>
        </div>

    )
}

export default Search
